mapper.select:
select *
from nic_techsupport
where user_id = #{0} and status_id != #{1}

mapper.update:
update nic_techsupport
set status_id =#{1}
where techsupport_id = #{0}

update nic_techsupport
set manager_id =#{1}
where techsupport_id = #{0}

update nic_techsupport
set techsupport_endtime =#{1}
where techsupport_id = #{0}

update nic_techsupport
set techsupport_feedback =#{1}
where techsupport_id = #{0}
